import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';

const LifeCountdown = () => {
  const [timeLeft, setTimeLeft] = useState({
    years: 0,
    months: 0,
    weeks: 0,
    days: 0,
    hours: 0,
    minutes: 0,
    seconds: 0
  });

  useEffect(() => {
    const interval = setInterval(() => {
      // Calculate time left based on life expectancy
      const now = new Date();
      const birthDate = new Date('1990-01-01'); // Example birth date
      const lifeExpectancy = 80; // Example life expectancy in years

      const endDate = new Date(birthDate);
      endDate.setFullYear(birthDate.getFullYear() + lifeExpectancy);

      const diff = endDate - now;

      if (diff > 0) {
        const years = Math.floor(diff / (1000 * 60 * 60 * 24 * 365));
        const months = Math.floor((diff % (1000 * 60 * 60 * 24 * 365)) / (1000 * 60 * 60 * 24 * 30));
        const weeks = Math.floor((diff % (1000 * 60 * 60 * 24 * 30)) / (1000 * 60 * 60 * 24 * 7));
        const days = Math.floor((diff % (1000 * 60 * 60 * 24 * 7)) / (1000 * 60 * 60 * 24));
        const hours = Math.floor((diff % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
        const minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));
        const seconds = Math.floor((diff % (1000 * 60)) / 1000);

        setTimeLeft({ years, months, weeks, days, hours, minutes, seconds });
      }
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  const timeUnits = [
    { label: 'Years', value: timeLeft.years },
    { label: 'Months', value: timeLeft.months },
    { label: 'Weeks', value: timeLeft.weeks },
    { label: 'Days', value: timeLeft.days },
    { label: 'Hours', value: timeLeft.hours },
    { label: 'Minutes', value: timeLeft.minutes },
    { label: 'Seconds', value: timeLeft.seconds }
  ];

  return (
    <div className="space-y-4">
      <motion.h3 
        className="text-xl font-semibold text-transparent bg-clip-text bg-gradient-to-r from-orange-500 to-pink-500"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.2 }}
      >
        Time Remaining
      </motion.h3>
      <div className="grid grid-cols-2 gap-3">
        {timeUnits.map((unit, index) => (
          <motion.div
            key={unit.label}
            className="bg-gray-800/50 backdrop-blur-sm p-3 rounded-xl border border-gray-700"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: index * 0.1 }}
            whileHover={{ scale: 1.05 }}
          >
            <div className="text-2xl font-bold text-orange-500">{unit.value}</div>
            <div className="text-sm text-gray-400">{unit.label}</div>
          </motion.div>
        ))}
      </div>
      <motion.div 
        className="text-sm text-gray-400 italic mt-4"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 1 }}
      >
        Make every moment count
      </motion.div>
    </div>
  );
};

export default LifeCountdown;
