import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../../context/AuthContext';
import { motion } from 'framer-motion';

const LifeCountdown = () => {
  const { user } = useAuth();
  const [countdown, setCountdown] = useState(null);
  const [displayUnit, setDisplayUnit] = useState('days');
  const [error, setError] = useState('');
  const [quote, setQuote] = useState('');

  useEffect(() => {
    const fetchLifeExpectancy = async () => {
      try {
        const config = {
          headers: {
            'x-auth-token': localStorage.getItem('token')
          }
        };
        const res = await axios.get('http://localhost:3001/api/user/life-expectancy', config);
        setCountdown(res.data);
      } catch (err) {
        setError('Failed to fetch life expectancy data');
      }
    };

    const fetchQuote = async () => {
      try {
        const res = await axios.get('https://api.quotable.io/random?tags=inspirational');
        setQuote(res.data.content);
      } catch (err) {
        console.error('Failed to fetch quote');
      }
    };

    if (user?.profile?.birthdate) {
      fetchLifeExpectancy();
      fetchQuote();
    }
  }, [user]);

  const formatTime = (days) => {
    switch (displayUnit) {
      case 'years':
        return Math.floor(days / 365);
      case 'months':
        return Math.floor(days / 30.44);
      case 'weeks':
        return Math.floor(days / 7);
      default:
        return days;
    }
  };

  const calculateProgress = () => {
    if (!countdown) return 0;
    const totalDays = countdown.totalDays;
    const daysLived = totalDays - countdown.daysRemaining;
    return (daysLived / totalDays) * 100;
  };

  if (!user?.profile?.birthdate) {
    return (
      <div className="text-center text-gray-600">
        Please complete your profile to see your life expectancy.
      </div>
    );
  }

  if (error) {
    return <div className="text-red-500">{error}</div>;
  }

  if (!countdown) {
    return <div className="text-gray-600">Loading...</div>;
  }

  return (
    <div className="max-w-2xl mx-auto p-6 bg-white rounded-lg shadow-lg">
      <div className="text-center mb-8">
        <div className="text-6xl font-bold text-blue-600 mb-4">
          {formatTime(countdown.daysRemaining)}
        </div>
        <div className="text-xl text-gray-600 mb-2">
          {displayUnit} remaining
        </div>
      </div>

      <div className="w-full bg-gray-200 rounded-full h-4 mb-6 overflow-hidden">
        <motion.div
          className="h-full rounded-full bg-gradient-to-r from-blue-500 via-blue-400 to-blue-500 bg-[length:200%_100%]"
          initial={{ width: 0, backgroundPosition: "0% 0%" }}
          animate={{ 
            width: `${calculateProgress()}%`,
            backgroundPosition: ["0% 0%", "100% 0%"],
            scale: [1, 1.02, 1]
          }}
          transition={{
            width: { duration: 1, ease: "easeOut" },
            backgroundPosition: {
              duration: 2,
              repeat: Infinity,
              ease: "linear"
            },
            scale: {
              duration: 2,
              repeat: Infinity,
              ease: "easeInOut"
            }
          }}
        />
      </div>
      <motion.div 
        className="text-center text-gray-600 mb-6"
        initial={{ opacity: 0, y: 10 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5, delay: 0.8 }}
      >
        {calculateProgress().toFixed(1)}% of life journey completed
      </motion.div>

      <div className="flex justify-center space-x-4 mb-8">
        {['days', 'weeks', 'months', 'years'].map((unit) => (
          <button
            key={unit}
            onClick={() => setDisplayUnit(unit)}
            className={`px-4 py-2 rounded-lg ${
              displayUnit === unit
                ? 'bg-blue-500 text-white'
                : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
            }`}
          >
            {unit.charAt(0).toUpperCase() + unit.slice(1)}
          </button>
        ))}
      </div>

      {quote && (
        <div className="text-center text-gray-600 italic border-t pt-6">
          "{quote}"
        </div>
      )}
    </div>
  );
};

export default LifeCountdown;
