import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import { countries } from '../../utils/countries';

const ProfileForm = () => {
  const { user, updateProfile } = useAuth();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    birthdate: '',
    country: '',
    gender: 'prefer-not-to-say',
    height: '',
    weight: '',
    lifestyle: {
      smoker: false,
      drinker: false,
      regularExercise: false,
      healthyDiet: false
    }
  });
  const [error, setError] = useState('');

  useEffect(() => {
    if (user?.profile) {
      setFormData({
        birthdate: user.profile.birthdate ? user.profile.birthdate.split('T')[0] : '',
        country: user.profile.country || '',
        gender: user.profile.gender || 'prefer-not-to-say',
        height: user.profile.height || '',
        weight: user.profile.weight || '',
        lifestyle: {
          smoker: user.profile.lifestyle?.smoker || false,
          drinker: user.profile.lifestyle?.drinker || false,
          regularExercise: user.profile.lifestyle?.regularExercise || false,
          healthyDiet: user.profile.lifestyle?.healthyDiet || false
        }
      });
    }
  }, [user]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await updateProfile(formData);
      navigate('/dashboard');
    } catch (err) {
      setError(err.toString());
    }
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;

    if (name.startsWith('lifestyle.')) {
      const lifestyleField = name.split('.')[1];
      setFormData(prev => ({
        ...prev,
        lifestyle: {
          ...prev.lifestyle,
          [lifestyleField]: type === 'checkbox' ? checked : value
        }
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: type === 'checkbox' ? checked : value
      }));
    }
  };

  return (
    <div className="min-h-screen pt-20 px-4">
      <div className="max-w-2xl mx-auto bg-gray-900/50 backdrop-blur-lg rounded-2xl border border-gray-800 p-6">
        <h2 className="text-2xl font-bold mb-6 text-gray-800">Profile Information</h2>
        {error && <div className="mb-4 text-red-500">{error}</div>}
        <form onSubmit={handleSubmit} className="space-y-6">
          {/* Basic Information */}
          <div className="space-y-4">
            <h3 className="text-xl font-semibold text-gray-700">Basic Information</h3>
            <div>
              <label className="block text-gray-700 text-sm font-bold mb-2">
                Birthdate
              </label>
              <input
                type="date"
                name="birthdate"
                value={formData.birthdate}
                onChange={handleChange}
                className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>
            <div>
              <label className="block text-gray-700 text-sm font-bold mb-2">
                Country
              </label>
              <select
                name="country"
                value={formData.country}
                onChange={handleChange}
                className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              >
                <option value="">Select a country</option>
                {countries.map(country => (
                  <option key={country.code} value={country.code}>
                    {country.name}
                  </option>
                ))}
              </select>
            </div>
            <div>
              <label className="block text-gray-700 text-sm font-bold mb-2">
                Gender
              </label>
              <select
                name="gender"
                value={formData.gender}
                onChange={handleChange}
                className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                <option value="prefer-not-to-say">Prefer not to say</option>
                <option value="male">Male</option>
                <option value="female">Female</option>
                <option value="other">Other</option>
              </select>
            </div>
          </div>

          {/* Physical Measurements */}
          <div className="space-y-4">
            <h3 className="text-xl font-semibold text-gray-700">Physical Measurements</h3>
            <div className="grid grid-cols-2 gap-4">
              <div>
                <label className="block text-gray-700 text-sm font-bold mb-2">
                  Height (meters)
                </label>
                <input
                  type="number"
                  name="height"
                  value={formData.height}
                  onChange={handleChange}
                  step="0.01"
                  min="0"
                  max="3"
                  className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>
              <div>
                <label className="block text-gray-700 text-sm font-bold mb-2">
                  Weight (kg)
                </label>
                <input
                  type="number"
                  name="weight"
                  value={formData.weight}
                  onChange={handleChange}
                  step="0.1"
                  min="0"
                  max="500"
                  className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>
            </div>
          </div>

          {/* Lifestyle Factors */}
          <div className="space-y-4">
            <h3 className="text-xl font-semibold text-gray-700">Lifestyle Factors</h3>
            <div className="grid grid-cols-2 gap-4">
              <div className="flex items-center">
                <input
                  type="checkbox"
                  name="lifestyle.smoker"
                  checked={formData.lifestyle.smoker}
                  onChange={handleChange}
                  className="h-4 w-4 text-blue-600"
                />
                <label className="ml-2 text-gray-700">Smoker</label>
              </div>
              <div className="flex items-center">
                <input
                  type="checkbox"
                  name="lifestyle.drinker"
                  checked={formData.lifestyle.drinker}
                  onChange={handleChange}
                  className="h-4 w-4 text-blue-600"
                />
                <label className="ml-2 text-gray-700">Regular Drinker</label>
              </div>
              <div className="flex items-center">
                <input
                  type="checkbox"
                  name="lifestyle.regularExercise"
                  checked={formData.lifestyle.regularExercise}
                  onChange={handleChange}
                  className="h-4 w-4 text-blue-600"
                />
                <label className="ml-2 text-gray-700">Regular Exercise</label>
              </div>
              <div className="flex items-center">
                <input
                  type="checkbox"
                  name="lifestyle.healthyDiet"
                  checked={formData.lifestyle.healthyDiet}
                  onChange={handleChange}
                  className="h-4 w-4 text-blue-600"
                />
                <label className="ml-2 text-gray-700">Healthy Diet</label>
              </div>
            </div>
          </div>

          <button
            type="submit"
            className="w-full bg-blue-500 text-white py-2 px-4 rounded-lg hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            Save Profile
          </button>
        </form>
      </div>
    </div>
  );
};

export default ProfileForm;
