import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { useAuth } from '../../context/AuthContext';
import { motion } from 'framer-motion';

// Custom hook for animated counter
const useAnimatedValue = (targetValue, duration = 1500) => {
  const [value, setValue] = useState(0);
  const startTimeRef = useRef(null);
  const frameRef = useRef(null);

  useEffect(() => {
    const startValue = 0;
    const animate = (timestamp) => {
      if (!startTimeRef.current) startTimeRef.current = timestamp;
      const progress = timestamp - startTimeRef.current;
      const percentage = Math.min(progress / duration, 1);

      setValue(startValue + (targetValue - startValue) * percentage);

      if (progress < duration) {
        frameRef.current = requestAnimationFrame(animate);
      }
    };

    frameRef.current = requestAnimationFrame(animate);

    return () => {
      if (frameRef.current) {
        cancelAnimationFrame(frameRef.current);
      }
    };
  }, [targetValue, duration]);

  return value;
};

const LifeCountdown = () => {
  const { user } = useAuth();
  const [countdown, setCountdown] = useState(null);
  const [displayUnit, setDisplayUnit] = useState('days');
  const [error, setError] = useState('');
  const [quote, setQuote] = useState('');

  // Create animated values for each time unit
  const animatedDays = useAnimatedValue(countdown?.daysRemaining || 0, 2000);
  const animatedWeeks = useAnimatedValue((countdown?.daysRemaining || 0) / 7, 2000);
  const animatedMonths = useAnimatedValue((countdown?.daysRemaining || 0) / 30.44, 2000);
  const animatedYears = useAnimatedValue((countdown?.daysRemaining || 0) / 365.25, 2000);

  useEffect(() => {
    const fetchLifeExpectancy = async () => {
      try {
        const config = {
          headers: {
            'x-auth-token': localStorage.getItem('token')
          }
        };
        const res = await axios.get('http://localhost:3001/api/user/life-expectancy', config);
        setCountdown(res.data);
      } catch (err) {
        setError('Failed to fetch life expectancy data');
      }
    };

    const fetchQuote = async () => {
      try {
        const res = await axios.get('https://api.quotable.io/random?tags=inspirational');
        setQuote(res.data.content);
      } catch (err) {
        console.error('Failed to fetch quote');
      }
    };

    if (user?.profile?.birthdate) {
      fetchLifeExpectancy();
      fetchQuote();
    }
  }, [user]);

  const formatNumber = (number) => {
    return Math.round(number).toLocaleString();
  };

  if (!user?.profile?.birthdate) {
    return (
      <motion.div 
        className="text-center text-gray-400"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
      >
        Please complete your profile to see your life expectancy.
      </motion.div>
    );
  }

  if (error) {
    return (
      <motion.div 
        className="text-red-400"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
      >
        {error}
      </motion.div>
    );
  }

  if (!countdown) {
    return (
      <motion.div 
        className="text-gray-400"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
      >
        Loading...
      </motion.div>
    );
  }

  return (
    <div className="space-y-6">
      <motion.div 
        className="text-center"
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <motion.div 
          className="text-6xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-orange-500 to-pink-500 mb-2"
          initial={{ scale: 0.8 }}
          animate={{ scale: 1 }}
          transition={{ type: "spring", stiffness: 300 }}
        >
          {formatNumber(animatedDays)}
        </motion.div>
        <div className="text-xl text-gray-400 mb-4">
          {displayUnit} remaining
        </div>
      </motion.div>

      <div className="flex justify-center space-x-3 mb-6">
        {['days', 'weeks', 'months', 'years'].map((unit) => (
          <motion.button
            key={unit}
            onClick={() => setDisplayUnit(unit)}
            className={`px-4 py-2 rounded-lg transition-colors ${
              displayUnit === unit
                ? 'bg-gradient-to-r from-orange-500 to-pink-500 text-white'
                : 'bg-gray-800/50 text-gray-300 hover:bg-gray-700/50'
            }`}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            {unit.charAt(0).toUpperCase() + unit.slice(1)}
          </motion.button>
        ))}
      </div>

      <div className="grid grid-cols-3 gap-4 mb-6">
        <motion.div 
          className="text-center"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2 }}
        >
          <div className="text-2xl font-bold text-white">
            {formatNumber(animatedWeeks)}
          </div>
          <div className="text-sm text-gray-400">weeks</div>
        </motion.div>
        <motion.div 
          className="text-center"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.3 }}
        >
          <div className="text-2xl font-bold text-white">
            {formatNumber(animatedMonths)}
          </div>
          <div className="text-sm text-gray-400">months</div>
        </motion.div>
        <motion.div 
          className="text-center"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.4 }}
        >
          <div className="text-2xl font-bold text-white">
            {formatNumber(animatedYears)}
          </div>
          <div className="text-sm text-gray-400">years</div>
        </motion.div>
      </div>

      {quote && (
        <motion.div 
          className="text-center text-gray-400 italic border-t border-gray-800 pt-4"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.5 }}
        >
          "{quote}"
        </motion.div>
      )}
    </div>
  );
};

export default LifeCountdown;
