import React, { useState, useEffect } from 'react';
import { CircularProgressbar, buildStyles } from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';
import LifeCountdown from './LifeCountdown';
import axios from 'axios';
import { useAuth } from '../../context/AuthContext';

const getRiskColor = (risk) => {
  switch (risk) {
    case 'high':
      return 'text-red-500';
    case 'medium':
      return 'text-yellow-500';
    case 'low':
      return 'text-green-500';
    default:
      return 'text-gray-500';
  }
};

const HealthMetricCard = ({ title, value, category, risk }) => (
  <div className="bg-white p-4 rounded-lg shadow-md">
    <h3 className="text-lg font-semibold mb-2">{title}</h3>
    <div className="flex justify-between items-center">
      <span className="text-2xl font-bold">{value}</span>
      <span className={`font-medium ${getRiskColor(risk)}`}>
        {category}
      </span>
    </div>
  </div>
);

const Dashboard = () => {
  const { user } = useAuth();
  const [lifeData, setLifeData] = useState(null);
  const [healthMetrics, setHealthMetrics] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchLifeData = async () => {
      try {
        const config = {
          headers: {
            'x-auth-token': localStorage.getItem('token')
          }
        };
        const res = await axios.get('http://localhost:3001/api/user/life-expectancy', config);
        setLifeData(res.data);
        setHealthMetrics(res.data.healthMetrics);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching life data:', err);
        setError('Failed to load life expectancy data');
        setLoading(false);
      }
    };

    if (user?.profile?.birthdate) {
      fetchLifeData();
    } else {
      setLoading(false);
      setError('Please complete your profile to see your dashboard.');
    }
  }, [user]);

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-xl text-gray-600">Loading your dashboard...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-xl text-gray-600">{error}</div>
      </div>
    );
  }

  return (
    <div className="max-w-6xl mx-auto px-4 py-8">
      <div className="grid grid-cols-1 md:grid-cols-12 gap-6">
        {/* Main Stats */}
        <div className="md:col-span-8 space-y-6">
          <div className="bg-white p-6 rounded-lg shadow-lg">
            <h2 className="text-2xl font-bold mb-4">Life Progress</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <LifeCountdown />
              </div>
              <div className="flex items-center justify-center">
                <div className="w-48 h-48">
                  <CircularProgressbar
                    value={parseFloat(lifeData.percentCompleted)}
                    text={`${parseFloat(lifeData.percentCompleted).toFixed(1)}%`}
                    styles={buildStyles({
                      textSize: '16px',
                      pathColor: `rgba(62, 152, 199, ${parseFloat(lifeData.percentCompleted) / 100})`,
                      textColor: '#2C5282',
                      trailColor: '#d6d6d6'
                    })}
                  />
                </div>
              </div>
            </div>
          </div>

          {/* Health Metrics Grid */}
          <div className="bg-white p-6 rounded-lg shadow-lg">
            <h2 className="text-2xl font-bold mb-4">Health Metrics</h2>
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
              {healthMetrics?.bmi && (
                <HealthMetricCard
                  title="BMI"
                  value={healthMetrics.bmi.value.toFixed(1)}
                  category={healthMetrics.bmi.category}
                  risk={healthMetrics.bmi.risk}
                />
              )}
              <HealthMetricCard
                title="Heart Rate"
                value={`${healthMetrics?.heartRate?.value || user.profile.healthMetrics.restingHeartRate} bpm`}
                category={healthMetrics?.heartRate?.zone || 'Normal'}
                risk={healthMetrics?.heartRate?.risk || 'low'}
              />
              <HealthMetricCard
                title="Blood Pressure"
                value={`${healthMetrics?.bloodPressure?.systolic}/${healthMetrics?.bloodPressure?.diastolic}`}
                category="Normal"
                risk="low"
              />
              <HealthMetricCard
                title="Sleep Quality"
                value={`${user.profile.lifestyle.avgHoursSlept} hrs`}
                category={healthMetrics?.sleep?.quality || 'Good'}
                risk={healthMetrics?.sleep?.risk || 'low'}
              />
              <HealthMetricCard
                title="Stress Level"
                value={healthMetrics?.stress?.level || 'Low'}
                category={healthMetrics?.stress?.level || 'Healthy'}
                risk={healthMetrics?.stress?.risk || 'low'}
              />
              <HealthMetricCard
                title="Exercise"
                value={user.profile.lifestyle.regularExercise ? 'Active' : 'Inactive'}
                category={user.profile.lifestyle.regularExercise ? 'Regular' : 'Irregular'}
                risk={user.profile.lifestyle.regularExercise ? 'low' : 'high'}
              />
            </div>
          </div>
        </div>

        {/* Sidebar */}
        <div className="md:col-span-4 space-y-6">
          {/* Recommendations */}
          <div className="bg-white p-6 rounded-lg shadow-lg">
            <h2 className="text-2xl font-bold mb-4">Health Insights</h2>
            <div className="space-y-4">
              {user.profile.lifestyle.avgHoursSlept >= 7 && (
                <div className="p-4 bg-green-50 border-l-4 border-green-500 rounded">
                  <h3 className="font-semibold text-green-700">Excellent Sleep Habits</h3>
                  <p className="text-sm text-green-600">Your consistent sleep schedule contributes to a longer, healthier life.</p>
                </div>
              )}
              {!user.profile.lifestyle.regularExercise && (
                <div className="p-4 bg-yellow-50 border-l-4 border-yellow-500 rounded">
                  <h3 className="font-semibold text-yellow-700">Exercise More</h3>
                  <p className="text-sm text-yellow-600">Increasing physical activity could add 2-3 years to your life expectancy.</p>
                </div>
              )}
              {user.profile.lifestyle.highStress && (
                <div className="p-4 bg-blue-50 border-l-4 border-blue-500 rounded">
                  <h3 className="font-semibold text-blue-700">Stress Management</h3>
                  <p className="text-sm text-blue-600">Consider meditation or yoga to reduce stress levels.</p>
                </div>
              )}
              {user.profile.lifestyle.smoker && (
                <div className="p-4 bg-red-50 border-l-4 border-red-500 rounded">
                  <h3 className="font-semibold text-red-700">Quit Smoking</h3>
                  <p className="text-sm text-red-600">Quitting smoking could add up to 10 years to your life expectancy.</p>
                </div>
              )}
            </div>
          </div>

          {/* Milestones */}
          <div className="bg-white p-6 rounded-lg shadow-lg">
            <h2 className="text-2xl font-bold mb-4">Life Milestones</h2>
            <div className="space-y-4">
              {lifeData && (
                <>
                  <div className="relative pl-4 border-l-2 border-blue-500">
                    <h3 className="font-semibold">5 Years</h3>
                    <p className="text-sm text-gray-600">
                      Age {Math.floor(lifeData.daysLived / 365) + 5}: Focus on preventive health checks
                    </p>
                  </div>
                  <div className="relative pl-4 border-l-2 border-blue-500">
                    <h3 className="font-semibold">10 Years</h3>
                    <p className="text-sm text-gray-600">
                      Age {Math.floor(lifeData.daysLived / 365) + 10}: Key health screening decade
                    </p>
                  </div>
                  <div className="relative pl-4 border-l-2 border-blue-500">
                    <h3 className="font-semibold">Golden Years</h3>
                    <p className="text-sm text-gray-600">
                      Age {Math.floor(lifeData.lifeExpectancy * 0.75)}: Retirement planning phase
                    </p>
                  </div>
                </>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
